#ifndef JUDGECORESERVER_CODE_RUNNER_RESULT_H_
#define JUDGECORESERVER_CODE_RUNNER_RESULT_H_

#include "shared/dto/resource_usage.h"

struct CodeRunnerResult
{
	/// Exit code of the program
	int exitCode;

	/// Message generated by the runner.
	/// If the program is successfully executed, this message should be empty.
	/// If the program was killed by the runner or returned non-zero exit code,
	/// this message should be the error message that describes the error.
	std::string message;
	
	/// Resource usage of the program
	himu::dto::ResourceUsage resourceUsage;
};

#endif// JUDGECORESERVER_CODE_RUNNER_RESULT_H_
